contracts/BondingCurveAMM.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title BondingCurveAMM
 * @notice Library implementing linear bonding curve mathematics for token pricing
 * @dev Price formula: P(x) = basePrice + k * x
 * @dev Uses fixed-point arithmetic with 1e18 scaling for precision
 */
library BondingCurveAMM {
    uint256 private constant FIXED_1 = 1e18;
    uint256 private constant USDC_DECIMALS = 6;
    uint256 private constant TOKEN_DECIMALS = 18;
    uint256 private constant USDC_SCALE = 10 ** USDC_DECIMALS;

    error InvalidAmount();
    error MathOverflow();
    error InsufficientLiquidity();

    /**
     * @notice Calculate tokens received for a given USDC amount
     * @dev Solves: cost = basePrice *  + k * S *  + k * () / 2
     * @dev Rearranged to quadratic: (k/2) *  + (k*S + basePrice) *  - cost = 0
     * @param S Current total supply in token units (18 decimals)
     * @param basePrice Base price in USDC (scaled to 1e18)
     * @param k Slope coefficient (scaled to 1e18)
     * @param usdcAmount Amount of USDC to spend (6 decimals)
     * @return deltaTokens Number of tokens that can be minted (18 decimals)
     */
    function tokensForBuy(
        uint256 S,
        uint256 basePrice,
        uint256 k,
        uint256 usdcAmount
    ) external pure returns (uint256 deltaTokens) {
        if (usdcAmount == 0) revert InvalidAmount();

        // Scale USDC amount to fixed-point (1e18)
        uint256 costScaled = (usdcAmount * FIXED_1) / USDC_SCALE;

        // Quadratic formula: (-b + sqrt(b + 4ac)) / 2a
        // where: a = k/2, b = k*S + basePrice, c = -cost

        uint256 a = k / 2; // k/2 in fixed-point
        uint256 b = (k * S) / FIXED_1 + basePrice; // k*S + basePrice

        // Calculate discriminant: b + 4 * a * cost
        // b (both in 1e18, so result is 1e36, scale back to 1e18)
        uint256 bSquared = (b * b) / FIXED_1;

        // 4 * a * cost
        uint256 fourACost = (4 * a * costScaled) / FIXED_1;

        uint256 discriminant = bSquared + fourACost;

        // Square root using Babylonian method
        uint256 sqrtDiscriminant = sqrt(discriminant);

        // (-b + sqrt) / 2a
        // Since b and sqrt are both in 1e18, their difference is 1e18
        if (sqrtDiscriminant < b) revert MathOverflow();

        uint256 numerator = sqrtDiscriminant - b;
        uint256 denominator = (2 * a);

        if (denominator == 0) {
            // Linear case: k = 0, so price is constant basePrice
            deltaTokens = (costScaled * FIXED_1) / basePrice;
        } else {
            // deltaTokens = numerator / denominator * FIXED_1
            deltaTokens = (numerator * FIXED_1) / denominator;
        }

        return deltaTokens;
    }

    /**
     * @notice Calculate USDC received for selling tokens
     * @dev Integral: refund = basePrice *  + k * S *  - k * () / 2
     * @param S Current total supply before burn (18 decimals)
     * @param basePrice Base price in USDC (scaled to 1e18)
     * @param k Slope coefficient (scaled to 1e18)
     * @param tokensToBurn Number of tokens to burn (18 decimals)
     * @return usdcOut Amount of USDC to return (6 decimals)
     */
    function usdcForSell(
        uint256 S,
        uint256 basePrice,
        uint256 k,
        uint256 tokensToBurn
    ) external pure returns (uint256 usdcOut) {
        if (tokensToBurn == 0) revert InvalidAmount();
        if (tokensToBurn > S) revert InsufficientLiquidity();

        // Calculate: basePrice * 
        uint256 baseTerm = (basePrice * tokensToBurn) / FIXED_1;

        // Calculate: k * S * 
        uint256 linearTerm = (k * S * tokensToBurn) / (FIXED_1 * FIXED_1);

        // Calculate: k * () / 2
        uint256 quadraticTerm = (k * tokensToBurn * tokensToBurn) / (2 * FIXED_1 * FIXED_1);

        // refund = baseTerm + linearTerm - quadraticTerm (all in 1e18)
        uint256 refundScaled = baseTerm + linearTerm - quadraticTerm;

        // Scale back to USDC decimals (6)
        usdcOut = (refundScaled * USDC_SCALE) / FIXED_1;

        return usdcOut;
    }

    /**
     * @notice Get current price for the next token
     * @param S Current total supply (18 decimals)
     * @param basePrice Base price (scaled to 1e18)
     * @param k Slope coefficient (scaled to 1e18)
     * @return price Price in USDC (6 decimals)
     */
    function getCurrentPrice(
        uint256 S,
        uint256 basePrice,
        uint256 k
    ) public pure returns (uint256 price) {
        // P(S) = basePrice + k * S
        uint256 priceScaled = basePrice + (k * S) / FIXED_1;

        // Convert to USDC decimals
        price = (priceScaled * USDC_SCALE) / FIXED_1;

        return price;
    }

    /**
     * @notice Calculate price impact for a buy transaction
     * @param S Current supply
     * @param basePrice Base price (scaled)
     * @param k Slope coefficient (scaled)
     * @param tokenAmount Amount of tokens to buy
     * @return priceBefore Price before transaction (USDC, 6 decimals)
     * @return priceAfter Price after transaction (USDC, 6 decimals)
     */
    function priceImpact(
        uint256 S,
        uint256 basePrice,
        uint256 k,
        uint256 tokenAmount
    ) external pure returns (uint256 priceBefore, uint256 priceAfter) {
        priceBefore = getCurrentPrice(S, basePrice, k);
        priceAfter = getCurrentPrice(S + tokenAmount, basePrice, k);
        return (priceBefore, priceAfter);
    }

    /**
     * @notice Babylonian method for calculating square root
     * @param y Value to find square root of
     * @return z Square root of y
     */
    function sqrt(uint256 y) internal pure returns (uint256 z) {
        if (y > 3) {
            z = y;
            uint256 x = y / 2 + 1;
            while (x < z) {
                z = x;
                x = (y / x + x) / 2;
            }
        } else if (y != 0) {
            z = 1;
        }
    }
}

contracts/CreatorToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./BondingCurveAMM.sol";
import "./interfaces/IPlatformTreasury.sol";

/**
 * @title CreatorToken
 * @notice ERC-20 token with bonding curve AMM, anti-dump protection, and milestone-based supply
 * @dev Implements linear bonding curve: P(x) = basePrice + k * x
 */
contract CreatorToken is ERC20Upgradeable, AccessControlUpgradeable, ReentrancyGuardUpgradeable, PausableUpgradeable {
    using SafeERC20 for IERC20;

    bytes32 public constant CREATOR_ROLE = keccak256("CREATOR_ROLE");
    bytes32 public constant ORACLE_ROLE = keccak256("ORACLE_ROLE");
    bytes32 public constant PLATFORM_ADMIN_ROLE = keccak256("PLATFORM_ADMIN_ROLE");

    // Bonding curve parameters (scaled to 1e18)
    uint256 public basePrice;           // Base price in USDC (scaled)
    uint256 public k;                   // Slope coefficient (scaled)
    uint256 public totalSupplyLimit;    // Current maximum supply allowed

    // Token addresses - Changed from immutable to regular for upgradeable pattern
    IERC20 public reserveToken;  // USDC
    IPlatformTreasury public treasury;

    // Fee configuration (in basis points)
    uint256 public creatorBuyFeeBps;    // Creator fee on buys (300 = 3%)
    uint256 public creatorSellFeeBps;   // Creator fee on sells (300 = 3%)
    uint256 public platformFeeBps;      // Platform fee (100 = 1%)
    uint256 public maxSellPercentBps;   // Max sell per tx (1500 = 15%)

    // Circuit breaker configuration
    uint256 public sellWindowSeconds;         // Time window for tracking sells (600 = 10 min)
    uint256 public sellWindowThresholdBps;    // Threshold to trigger breaker (500 = 5%)
    uint256 public sellsPausedUntil;          // Timestamp until sells are paused

    // Sell tracking for circuit breaker
    struct SellRecord {
        uint256 amount;
        uint256 timestamp;
    }
    SellRecord[] public sellHistory;
    uint256 private sellHistoryIndex;

    // Creator address
    address public creator;

    // Minimum tokens required for access
    uint256 public minTokensForAccess;

    // Reserve tracking
    uint256 public reserveBalance;  // USDC held in contract

    // Events
    event Buy(
        address indexed buyer,
        uint256 usdcIn,
        uint256 tokensMinted,
        uint256 priceAfter
    );

    event Sell(
        address indexed seller,
        uint256 tokensBurned,
        uint256 usdcOut,
        uint256 priceAfter
    );

    event SupplyUnlocked(uint256 newSupply, uint256 milestone);

    event CircuitBreakerTriggered(
        uint256 windowSellAmount,
        uint256 circulatingSupply,
        uint256 pausedUntil
    );

    event FeesForwarded(uint256 platformFee, uint256 creatorFee);

    event ConfigUpdated(string param, uint256 value);

    // Errors
    error SellTooLarge(uint256 attempted, uint256 maxAllowed);
    error SlippageExceeded(uint256 expected, uint256 actual);
    error SellsPausedUntil(uint256 until);
    error NotEnoughLiquidity();
    error MilestoneNotReached();
    error SupplyLimitExceeded();
    error InvalidAmount();
    error ZeroAddress();
    error InvalidConfiguration();

    /**
     * @notice Initialize the creator token (for use with factory/proxy pattern)
     * @dev Called by factory after clone deployment.
     */
    function initialize(
        string memory name,
        string memory symbol,
        address _creator,
        address _reserveToken,
        address _treasury,
        uint256 _basePrice,
        uint256 _k,
        uint256 _totalSupplyLimit,
        uint256 _minTokensForAccess,
        uint256 _creatorBuyFeeBps,
        uint256 _creatorSellFeeBps,
        uint256 _platformFeeBps,
        uint256 _maxSellPercentBps,
        uint256 _sellWindowSeconds,
        uint256 _sellWindowThresholdBps
    ) external initializer {
        require(_creator != address(0), "Invalid creator");
        require(_reserveToken != address(0), "Invalid reserve token");
        require(_treasury != address(0), "Invalid treasury");

        // Initialize parent contracts
        __ERC20_init(name, symbol);
        __AccessControl_init();
        __ReentrancyGuard_init();
        __Pausable_init();

        creator = _creator;
        basePrice = _basePrice;
        k = _k;
        totalSupplyLimit = _totalSupplyLimit;
        minTokensForAccess = _minTokensForAccess;

        reserveToken = IERC20(_reserveToken);
        treasury = IPlatformTreasury(_treasury);

        creatorBuyFeeBps = _creatorBuyFeeBps;
        creatorSellFeeBps = _creatorSellFeeBps;
        platformFeeBps = _platformFeeBps;
        maxSellPercentBps = _maxSellPercentBps;

        sellWindowSeconds = _sellWindowSeconds;
        sellWindowThresholdBps = _sellWindowThresholdBps;

        // Setup roles
        _grantRole(DEFAULT_ADMIN_ROLE, _creator);
        _grantRole(CREATOR_ROLE, _creator);
        _grantRole(PLATFORM_ADMIN_ROLE, msg.sender); // Factory is admin
    }

    constructor() {
        // Disable initializers for the implementation contract
        _disableInitializers();
    }

    /**
     * @notice Buy tokens with USDC
     * @param usdcAmount Amount of USDC to spend
     * @param minTokensOut Minimum tokens expected (slippage protection)
     * @param recipient Address to receive tokens
     * @return tokensMinted Number of tokens minted
     */
    function buyWithUSDC(
        uint256 usdcAmount,
        uint256 minTokensOut,
        address recipient
    ) external nonReentrant whenNotPaused returns (uint256 tokensMinted) {
        if (usdcAmount == 0) revert InvalidAmount();
        if (recipient == address(0)) revert ZeroAddress();

        // Calculate tokens to mint using bonding curve
        tokensMinted = BondingCurveAMM.tokensForBuy(
            totalSupply(),
            basePrice,
            k,
            usdcAmount
        );

        // Check slippage
        if (tokensMinted < minTokensOut) {
            revert SlippageExceeded(minTokensOut, tokensMinted);
        }

        // Check supply limit
        if (totalSupply() + tokensMinted > totalSupplyLimit) {
            revert SupplyLimitExceeded();
        }

        // Calculate fees
        uint256 platformFee = (usdcAmount * platformFeeBps) / 10000;
        uint256 creatorFee = (usdcAmount * creatorBuyFeeBps) / 10000;
        uint256 totalFees = platformFee + creatorFee;
        uint256 amountToReserve = usdcAmount - totalFees;

        // Transfer USDC from buyer
        reserveToken.safeTransferFrom(msg.sender, address(this), usdcAmount);

        // Forward fees to treasury
        if (totalFees > 0) {
            reserveToken.safeApprove(address(treasury), totalFees);
            treasury.depositFees(creator, creatorFee, platformFee);
            emit FeesForwarded(platformFee, creatorFee);
        }

        // Update reserve
        reserveBalance += amountToReserve;

        // Mint tokens
        _mint(recipient, tokensMinted);

        // Get price after
        uint256 priceAfter = BondingCurveAMM.getCurrentPrice(
            totalSupply(),
            basePrice,
            k
        );

        emit Buy(msg.sender, usdcAmount, tokensMinted, priceAfter);

        return tokensMinted;
    }

    /**
     * @notice Sell tokens for USDC
     * @param tokensToSell Amount of tokens to sell
     * @param minUsdcOut Minimum USDC expected (slippage protection)
     * @param recipient Address to receive USDC
     * @return usdcReturned Amount of USDC returned
     */
    function sellToUSDC(
        uint256 tokensToSell,
        uint256 minUsdcOut,
        address recipient
    ) external nonReentrant whenNotPaused returns (uint256 usdcReturned) {
        if (tokensToSell == 0) revert InvalidAmount();
        if (recipient == address(0)) revert ZeroAddress();

        // Check if sells are paused due to circuit breaker
        if (block.timestamp < sellsPausedUntil) {
            revert SellsPausedUntil(sellsPausedUntil);
        }

        // Check per-transaction sell limit (15% of balance)
        uint256 sellerBalance = balanceOf(msg.sender);
        uint256 maxSellAmount = (sellerBalance * maxSellPercentBps) / 10000;

        if (tokensToSell > maxSellAmount) {
            revert SellTooLarge(tokensToSell, maxSellAmount);
        }

        // Calculate USDC to return using bonding curve
        usdcReturned = BondingCurveAMM.usdcForSell(
            totalSupply(),
            basePrice,
            k,
            tokensToSell
        );

        // Calculate fees
        uint256 platformFee = (usdcReturned * platformFeeBps) / 10000;
        uint256 creatorFee = (usdcReturned * creatorSellFeeBps) / 10000;
        uint256 totalFees = platformFee + creatorFee;
        uint256 netUsdcOut = usdcReturned - totalFees;

        // Check slippage
        if (netUsdcOut < minUsdcOut) {
            revert SlippageExceeded(minUsdcOut, netUsdcOut);
        }

        // Check liquidity
        if (usdcReturned > reserveBalance) {
            revert NotEnoughLiquidity();
        }

        // Burn tokens first
        _burn(msg.sender, tokensToSell);

        // Update reserve
        reserveBalance -= usdcReturned;

        // Forward fees to treasury
        if (totalFees > 0) {
            reserveToken.safeApprove(address(treasury), totalFees);
            treasury.depositFees(creator, creatorFee, platformFee);
            emit FeesForwarded(platformFee, creatorFee);
        }

        // Transfer net USDC to seller
        reserveToken.safeTransfer(recipient, netUsdcOut);

        // Update sell tracking and check circuit breaker
        _updateSellTracking(tokensToSell);

        // Get price after
        uint256 priceAfter = BondingCurveAMM.getCurrentPrice(
            totalSupply(),
            basePrice,
            k
        );

        emit Sell(msg.sender, tokensToSell, netUsdcOut, priceAfter);

        return netUsdcOut;
    }

    /**
     * @notice Get buy quote for USDC amount
     * @param usdcAmount Amount of USDC
     * @return tokensOut Expected tokens (before fees)
     */
    function getBuyQuote(uint256 usdcAmount) external view returns (uint256 tokensOut) {
        return BondingCurveAMM.tokensForBuy(totalSupply(), basePrice, k, usdcAmount);
    }

    /**
     * @notice Get sell quote for token amount
     * @param tokenAmount Amount of tokens
     * @return usdcOut Expected USDC (before fees)
     */
    function getSellQuote(uint256 tokenAmount) external view returns (uint256 usdcOut) {
        return BondingCurveAMM.usdcForSell(totalSupply(), basePrice, k, tokenAmount);
    }

    /**
     * @notice Get current price for next token
     * @return price Current price in USDC
     */
    function getCurrentPrice() external view returns (uint256 price) {
        return BondingCurveAMM.getCurrentPrice(totalSupply(), basePrice, k);
    }

    /**
     * @notice Unlock additional supply after milestone verification
     * @dev Only callable by ORACLE_ROLE
     * @param newSupply New supply limit
     * @param milestone Milestone identifier
     */
    function unlockSupplyOnMilestone(uint256 newSupply, uint256 milestone) 
        external 
        onlyRole(ORACLE_ROLE) 
    {
        if (newSupply <= totalSupplyLimit) revert InvalidConfiguration();

        totalSupplyLimit = newSupply;

        emit SupplyUnlocked(newSupply, milestone);
    }

    /**
     * @notice Manually pause sells (emergency)
     * @param untilTimestamp Timestamp until which sells are paused
     */
    function pauseSells(uint256 untilTimestamp) 
        external 
        onlyRole(PLATFORM_ADMIN_ROLE) 
    {
        sellsPausedUntil = untilTimestamp;
    }

    /**
     * @notice Update circuit breaker to track sells and trigger pause if needed
     * @param tokenAmount Amount of tokens sold
     */
    function _updateSellTracking(uint256 tokenAmount) private {
        // Add current sell to history
        sellHistory.push(SellRecord({
            amount: tokenAmount,
            timestamp: block.timestamp
        }));

        // Calculate total sells in window
        uint256 windowStart = block.timestamp - sellWindowSeconds;
        uint256 totalSellsInWindow = 0;

        for (uint256 i = 0; i < sellHistory.length; i++) {
            if (sellHistory[i].timestamp >= windowStart) {
                totalSellsInWindow += sellHistory[i].amount;
            }
        }

        // Check if threshold exceeded
        uint256 currentSupply = totalSupply();
        uint256 sellThreshold = (currentSupply * sellWindowThresholdBps) / 10000;

        if (totalSellsInWindow >= sellThreshold) {
            // Trigger circuit breaker - pause sells for 60 minutes
            sellsPausedUntil = block.timestamp + 1 hours;

            emit CircuitBreakerTriggered(
                totalSellsInWindow,
                currentSupply,
                sellsPausedUntil
            );
        }

        // Clean old entries (gas optimization)
        _cleanOldSellRecords(windowStart);
    }

    /**
     * @notice Remove old sell records outside the window
     * @param windowStart Timestamp of window start
     */
    function _cleanOldSellRecords(uint256 windowStart) private {
        uint256 removeCount = 0;

        for (uint256 i = 0; i < sellHistory.length; i++) {
            if (sellHistory[i].timestamp < windowStart) {
                removeCount++;
            } else {
                break;
            }
        }

        if (removeCount > 0) {
            for (uint256 i = 0; i < sellHistory.length - removeCount; i++) {
                sellHistory[i] = sellHistory[i + removeCount];
            }

            for (uint256 i = 0; i < removeCount; i++) {
                sellHistory.pop();
            }
        }
    }

    /**
     * @notice Pause all token operations (emergency)
     */
    function pause() external onlyRole(PLATFORM_ADMIN_ROLE) {
        _pause();
    }

    /**
     * @notice Unpause token operations
     */
    function unpause() external onlyRole(PLATFORM_ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @notice Update minimum tokens for access
     * @param newMinimum New minimum token amount
     */
    function setMinTokensForAccess(uint256 newMinimum) 
        external 
        onlyRole(CREATOR_ROLE) 
    {
        minTokensForAccess = newMinimum;
        emit ConfigUpdated("minTokensForAccess", newMinimum);
    }

    /**
     * @notice Check if address has access (holds enough tokens)
     * @param user Address to check
     * @return hasAccess True if user has minimum tokens
     */
    function hasAccess(address user) external view returns (bool) {
        return balanceOf(user) >= minTokensForAccess;
    }

    /**
     * @notice Get sell window status
     * @return totalSells Total tokens sold in current window
     * @return threshold Threshold to trigger circuit breaker
     * @return isPaused Whether sells are currently paused
     */
    function getSellWindowStatus() 
        external 
        view 
        returns (
            uint256 totalSells,
            uint256 threshold,
            bool isPaused
        ) 
    {
        uint256 windowStart = block.timestamp - sellWindowSeconds;
        totalSells = 0;

        for (uint256 i = 0; i < sellHistory.length; i++) {
            if (sellHistory[i].timestamp >= windowStart) {
                totalSells += sellHistory[i].amount;
            }
        }

        threshold = (totalSupply() * sellWindowThresholdBps) / 10000;
        isPaused = block.timestamp < sellsPausedUntil;

        return (totalSells, threshold, isPaused);
    }
}

contracts/CreatorTokenFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./CreatorToken.sol";

/**
 * @title CreatorTokenFactory
 * @notice Factory for deploying CreatorToken instances using minimal proxies (EIP-1167)
 * @dev Implements cheap clone pattern for gas-efficient token deployment
 */
contract CreatorTokenFactory is AccessControl, ReentrancyGuard {
    bytes32 public constant PLATFORM_ADMIN_ROLE = keccak256("PLATFORM_ADMIN_ROLE");

    /// @notice CreatorToken implementation address
    address public implementation;

    /// @notice PlatformTreasury address
    address public treasury;

    /// @notice Trusted forwarder for meta-transactions
    address public trustedForwarder;

    /// @notice Platform fee in basis points (100 = 1%)
    uint256 public platformFeeBps;

    /// @notice Reserve token (USDC) address
    address public reserveToken;

    /// @notice Mapping of creator to their deployed tokens
    mapping(address => address[]) public creatorTokens;

    /// @notice Array of all deployed tokens
    address[] public allTokens;

    /// @notice Mapping to check if address is a valid creator token
    mapping(address => bool) public isCreatorToken;

    struct CreateParams {
        address creator;
        string name;
        string symbol;
        uint256 basePrice;          // Scaled to 1e18
        uint256 k;                  // Scaled to 1e18
        uint256 initialSupplyLimit;
        uint256 minTokensForAccess;
        uint256 creatorBuyFeeBps;
        uint256 creatorSellFeeBps;
        uint256 maxSellPercentBps;
        uint256 sellWindowSeconds;
        uint256 sellWindowThresholdBps;
    }

    event CreatorTokenCreated(
        address indexed creator,
        address indexed token,
        string name,
        string symbol
    );
    event PlatformFeeUpdated(uint256 newBps);
    event TrustedForwarderUpdated(address newForwarder);
    event ImplementationUpdated(address newImplementation);

    error ZeroAddress();
    error InvalidConfiguration();
    error Unauthorized();

    constructor(
        address _implementation,
        address _treasury,
        address _reserveToken,
        address _trustedForwarder,
        uint256 _platformFeeBps
    ) {
        if (
            _implementation == address(0) ||
            _treasury == address(0) ||
            _reserveToken == address(0)
        ) {
            revert ZeroAddress();
        }

        implementation = _implementation;
        treasury = _treasury;
        reserveToken = _reserveToken;
        trustedForwarder = _trustedForwarder;
        platformFeeBps = _platformFeeBps;

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(PLATFORM_ADMIN_ROLE, msg.sender);
    }

    /**
     * @notice Create a new creator token using minimal proxy pattern
     * @param params Creation parameters
     * @return token Address of the newly created token
     */
    function createCreatorToken(CreateParams calldata params) 
        external 
        nonReentrant 
        returns (address token) 
    {
        if (params.creator == address(0)) revert ZeroAddress();

        // Validate parameters
        if (params.basePrice == 0 || params.initialSupplyLimit == 0) {
            revert InvalidConfiguration();
        }

        // Deploy minimal proxy clone
        token = Clones.clone(implementation);

        // Initialize the token
        CreatorToken(token).initialize(
            params.name,
            params.symbol,
            params.creator,
            reserveToken,
            treasury,
            params.basePrice,
            params.k,
            params.initialSupplyLimit,
            params.minTokensForAccess,
            params.creatorBuyFeeBps,
            params.creatorSellFeeBps,
            platformFeeBps,
            params.maxSellPercentBps,
            params.sellWindowSeconds,
            params.sellWindowThresholdBps
        );

        // Grant CREATOR_TOKEN_ROLE to the token in treasury
        // This allows the token to deposit fees
        // Note: This requires treasury to have granted this factory PLATFORM_ADMIN_ROLE

        // Register token
        creatorTokens[params.creator].push(token);
        allTokens.push(token);
        isCreatorToken[token] = true;

        emit CreatorTokenCreated(params.creator, token, params.name, params.symbol);

        return token;
    }

    /**
     * @notice Get all tokens created by a creator
     * @param creator Creator address
     * @return tokens Array of token addresses
     */
    function getCreatorTokens(address creator) 
        external 
        view 
        returns (address[] memory tokens) 
    {
        return creatorTokens[creator];
    }

    /**
     * @notice Get total number of tokens deployed
     * @return count Total token count
     */
    function getTotalTokens() external view returns (uint256 count) {
        return allTokens.length;
    }

    /**
     * @notice Get all deployed tokens
     * @return tokens Array of all token addresses
     */
    function getAllTokens() external view returns (address[] memory tokens) {
        return allTokens;
    }

    /**
     * @notice Update platform fee
     * @param newBps New fee in basis points
     */
    function setPlatformFeeBps(uint256 newBps) 
        external 
        onlyRole(PLATFORM_ADMIN_ROLE) 
    {
        if (newBps > 1000) revert InvalidConfiguration(); // Max 10%
        platformFeeBps = newBps;
        emit PlatformFeeUpdated(newBps);
    }

    /**
     * @notice Update trusted forwarder
     * @param newForwarder New forwarder address
     */
    function setTrustedForwarder(address newForwarder) 
        external 
        onlyRole(PLATFORM_ADMIN_ROLE) 
    {
        trustedForwarder = newForwarder;
        emit TrustedForwarderUpdated(newForwarder);
    }

    /**
     * @notice Update implementation address
     * @param newImplementation New implementation address
     */
    function setImplementation(address newImplementation) 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        if (newImplementation == address(0)) revert ZeroAddress();
        implementation = newImplementation;
        emit ImplementationUpdated(newImplementation);
    }

    /**
     * @notice Update treasury address
     * @param newTreasury New treasury address
     */
    function setTreasury(address newTreasury) 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        if (newTreasury == address(0)) revert ZeroAddress();
        treasury = newTreasury;
    }
}

contracts/interfaces/IPlatformTreasury.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

interface IPlatformTreasury {
    function depositFees(
        address creator,
        uint256 creatorFee,
        uint256 platformFee
    ) external;

    function withdrawCreatorFees(uint256 amount) external;

    function withdrawPlatformFees(uint256 amount) external;

    function getVestedAmount(address creator) external view returns (uint256);

    function getCreatorBalance(address creator) 
        external 
        view 
        returns (
            uint256 total,
            uint256 withdrawn,
            uint256 vested,
            uint256 unvested
        );
}

